      setName(event.target.value);

  };
  const onSubmit = (event) => {
      event.preventDefault();
      setFullName(name);
  };
    return (
        <>
        <div>
        <form onSubmit={onSubmit}>
       <div >
           <h1>Hello {FullName} </h1>
          <input type="text"  
          placeholder="Enter Your name" 
          onChange={InputEvent}
          value={name}
          />
          <br/>
